import React, {useState, useEffect} from 'react'
import styled from 'styled-components'
// import ShowMore from 'react-show-more';

import { SectionCol, Row } from "./Container"
import { calcCourseNumColor } from "../../functions/utils"

// import AddShoppingCartIcon from '@material-ui/icons/AddShoppingCart';
// import RemoveShoppingCartIcon from '@material-ui/icons/RemoveShoppingCart';

// import CourseInfo from "./../CourseInfo";
// import {SubContainer, SubTitle, SubText} from "./../CourseInfo"


// import { CartButton, }  from './common/CourseCard'
// import { CourseNumContainer, TitleContainer }  from './common/CourseCard'
import Modal from 'react-modal';



const customStyles = {
    content : {
      top: '50%',
      left: '50%',
      right: 'auto',
      bottom: 'auto',
      marginRight: '-50%',
      transform: 'translate(-50%, -50%)',
      minWidth: '500px',
      minHeight: '250px',
      maxWidth: '500px',
      maxHeight: '500px',
      boxShadow: '0px 2px 3px 1px rgb(196, 196, 196, 0.5);'
    }
  };
const AddRemoveContainer = styled.div`
  display: flex;
  justify-content: flex-end;
  margin-top: 30px;
`
// const AddRemoveButton = styled.button`
//   background-color: #20c94d;
//   color: #ffffff;
//   font-size: 14px;
//   font-weight: 600;
//   border: none;
//   border-radius: 5px;
//   width: 50%;
//   min-height: 30px;
// `

// export const CourseTitle = styled.div`
//   font-size: 17px;
//   font-weight: 600;
//   margin-bottom: 12px;
// `;

export const SubContainer = styled.div`
  margin-bottom: 8px;
`;

export const SubTitle = styled.span`
  font-size: 16px;
  font-weight: 500;
  margin-left: 25px;
`;

export const SubText = styled.span`
  font-size: 14px;
  font-weight: 400;
`;

// type courseType = {
//     dept: string;
//     number: number;
//     title: string;
//     crosslisted?: string[];
//     prereqs?: string[];
//     description: string;
//   };

type CourseInfoProps = {
    course: courseType;
    visible: boolean;
    setVisible: (vis:boolean) => void;
    addCourse: (course: courseType) => void;
    removeCourse: (course: courseType) => void;
    cart: Set<string>;
};

const CourseInfo = ({
    course,
    visible,
    setVisible,
    addCourse,
    removeCourse,
    cart}: CourseInfoProps) => {

    useEffect(() => {
    }, [visible])

    const closeModal = () => {
        setVisible(false)
    }

    const courseInCart = (courseCart: courseType): boolean => {
        let stringCourse = JSON.stringify(courseCart)
        return cart.has(stringCourse)
    }

    return (
      <>
            <Modal isOpen={visible} onRequestClose={closeModal} style={customStyles}>
              <TitleContainer>
              <CourseNumContainer courseNum={course.number}>CIS {course.number}</CourseNumContainer>
                {/* <CourseTitle>{course.dept} {' '} {course.number} - {course.title}</CourseTitle> */}
                <CourseTitle> - {course.title}</CourseTitle>
              </TitleContainer>

                <SubContainer>
                    <SubTitle>Description: </SubTitle>
                    <SubText>{course.description}</SubText>
                </SubContainer>
                <SubContainer>
                    <SubTitle>Prerequisite: </SubTitle>
                    <SubText>{(course.prereqs && course.prereqs.length != 0) ? "" : "None"}</SubText>
                    {course.prereqs && course.prereqs.length != 0 &&
                                course.prereqs.map( (prereq) => (
                                    <SubText>{prereq}, </SubText>
                                ))}
                </SubContainer>

                <AddRemoveContainer>
                    {courseInCart(course) ? <CartButton backgroundColor={false} onClick={() => removeCourse(course)}> Remove </CartButton> :
                    <CartButton backgroundColor={true} onClick={() => addCourse(course)}> Add </CartButton>}
                </AddRemoveContainer>
            </Modal>

      </>
    );
  };

  export default CourseInfo;
















const CourseContainer = styled.div`
  display: flex;
  border-radius: 12px;
  height: auto;
  width: auto;
  padding: 10px;
  background: #ffffff;
  box-shadow: 5px 5px 20px #f2f2f2;
  align-items: top;
  flex-direction: column;
  transition-duration: 0.7s;
  &:hover {
    transform: scale(1.05);
    transition-duration: 0.7s;
  }
`;

export const TitleContainer = styled.div`
  display: flex;
  align-items: center;
  padding: 8px 0;
`;

const CourseTitle = styled.div`
  font-weight: bold;
  font-size: 17px;
  margin: 0;
`;

type ButtonProps = {
  backgroundColor: boolean;
}

// const CartButton = styled.button<ButtonProps>`
//   background-color: ${({ backgroundColor }) => backgroundColor ? '#3262a8' : '#ab113d'};  
//   border: none;
//   color: white;
//   padding: 15px 32px;
//   border-radius: 10%;
//   text-align: center;
//   text-decoration: none;
//   display: inline-block;
//   font-size: 16px;
// `;
// #ab113d


export const CartButton = styled.button<ButtonProps>`
  background-color: ${({ backgroundColor }) => backgroundColor ? '#3262a8' : '#ab113d'};  
  border: 1px solid white;
  box-shadow: 0px 2px 0 white, 2px 4px 6px #eee;
  font-weight: 800;
  font-size: 18px;
  color: white;
  letter-spacing: 1px;
  -webkit-transition: all 150ms linear;
  transition: all 150ms linear;
  border-radius: 10%;
  &:hover {
    border: 1px solid rgba(0, 0, 0, 0.05);
    box-shadow: 1px 1px 2px rgba(255, 255, 255, 0.2);
    color: #c9d7f2;
    text-decoration: none;
    text-shadow: -1px -1px 0 #136a65;
    -webkit-transition: all 250ms linear;
    transition: all 250ms linear;
  }
`;

  type CourseNumContainerProps = {
    courseNum: number;
  }

  export const CourseNumContainer = styled.div<CourseNumContainerProps>`
    display: flex;
    border-radius: 5px;
    min-height: 35px;
    min-width: 60px;
    max-height: 40px;
    background: ${({ courseNum }) => calcCourseNumColor(courseNum) || '#588afc'};
    justify-content: center;
    align-items: center;
    margin-right: 10px;
    color: #ffffff;
    font-weight: 600;
    font-size: 15px;
  `;

  const CourseDesc = styled.p`
    font-size: 13px;
    margin: 0 0 0 45px;
  `;

  const IconContainer = styled.div`
    padding-top: 8px;
    padding-right: 5px;
    display: flex;
    justify-content: end;
    flex-direction: column;
    float: right;
  `
  type courseType = {
    dept: string;
    number: number;
    title: string;
    crosslisted?: string[];
    prereqs?: string[];
    description: string;
  };

  interface CourseCardProps {
    dept: string;
    number: number;
    title: string;
    crosslisted?: string[];
    prereqs?: string[];
    description: string;
    cart: Set<string>;
    addCourse: (course: courseType) => void;
    removeCourse: (course: courseType) => void;
  }


  export const CourseCard = ({
    dept, 
    number,
    title,
    crosslisted,
    prereqs,
    description,
    cart,
    addCourse,
    removeCourse
    }: CourseCardProps) => {

    const [courseInfo, setCourseInfo] = useState<courseType>();
    const [showInfo, setShowInfo] = useState<boolean>(false);

    useEffect(() => {
      console.log("changed")
    }, [showInfo])

    const showCourseInfo = (course: courseType): void => {
      setCourseInfo(course)
      setShowInfo(true)
    }

    const courseInCart = (course: courseType):boolean => {
      let stringCourse = JSON.stringify(course)
      return cart.has(stringCourse)
    }

    return (
    <>
    <CourseContainer>
      <Row>
      <SectionCol percent="85%" onClick={() => showCourseInfo({dept, number, title, crosslisted, prereqs, description})}>
          <TitleContainer>
            <CourseNumContainer courseNum={number}>CIS {number}</CourseNumContainer>
            <CourseTitle key={`${dept}-${number}`}>{title}</CourseTitle>
          </TitleContainer>
          <CourseDesc>
          <SubContainer>
            <SubTitle>Prerequisites: </SubTitle>
            <SubText>{(prereqs && prereqs.length != 0) ? "" : "None"}</SubText>
            {prereqs && prereqs.length != 0 &&
                        prereqs.map( (prereq) => (
                            <SubText>{prereq}, </SubText>
                        ))}
          </SubContainer>
            {/* <ShowMore lines={3} more='Show more'>
              {description}
            </ShowMore> */}
          </CourseDesc>
      </SectionCol>

      <SectionCol percent="15%">
        {/* If course in cart, show delete from cart icon and allow user to delete, opposite otherwise */}
        <IconContainer>
          {courseInCart({dept, number, title, crosslisted, prereqs, description}) ? <CartButton backgroundColor={false} onClick={() => removeCourse({dept, number, title, crosslisted, prereqs, description})}>Remove</CartButton>
          : <CartButton backgroundColor={true} onClick={() => addCourse({dept, number, title, crosslisted, prereqs, description})}>Add</CartButton>}

          {/* {courseInCart({dept, number, title, crosslisted, prereqs, description}) ? <RemoveShoppingCartIcon fontSize='default'  onClick={() => removeCourse({dept, number, title, crosslisted, prereqs, description})}></RemoveShoppingCartIcon>
          : <AddShoppingCartIcon fontSize='default'  onClick={() => addCourse({dept, number, title, crosslisted, prereqs, description})}></AddShoppingCartIcon>}  */}
        </IconContainer>
      </SectionCol>
      </Row>

    </CourseContainer>

    {courseInfo && 
    <CourseInfo course={courseInfo} visible={showInfo} setVisible={setShowInfo} 
        addCourse={addCourse} removeCourse={removeCourse} cart={cart}></CourseInfo>} 
    </>
    )
  }
